// n8n Code Node: Execution Data Normalization for SQL & Slack
const inputItems = $input.all();
const outputItems = [];

// Helper Functions
function sanitizeForSQL(text) {
  if (!text) return null;
  if (typeof text !== 'string') {
    text = String(text);
  }
  return text
    .replace(/'/g, "''")      // Escape single quotes for SQL
    .replace(/\\/g, "\\\\")   // Escape backslashes
    .replace(/\0/g, "")       // Remove null characters
    .replace(/\r\n/g, " ")    // Replace CRLF with spaces
    .replace(/\n/g, " ")      // Replace LF with spaces
    .replace(/\r/g, " ")      // Replace CR with spaces
    .replace(/\t/g, " ")      // Replace tabs with spaces
    .replace(/\s+/g, " ")     // Collapse multiple spaces
    .trim();
}

function truncateText(text, maxLength = 4000) {
  if (!text) return null;
  if (typeof text !== 'string') {
    text = String(text);
  }
  if (text.length <= maxLength) return text;
  return text.substring(0, maxLength - 3) + '...';
}

function formatSlackText(text, maxLength = 1000) {
  if (!text) return null;

  const truncated = truncateText(text, maxLength);

  // Escape Slack special characters
  return truncated
    .replace(/&/g, '&amp;')
    .replace(/</g, '&lt;')
    .replace(/>/g, '&gt;');
}

function determineExecutionStatus(execution) {
  if (execution.error) return 'error';
  if (execution.retryOf) return 'retry';
  return 'success';
}

function generateSlackMessage(normalized) {
  const status = normalized.executionStatus.toUpperCase();
  const emoji = normalized.executionStatus === 'error' ? 'ðŸ”´' : 
                normalized.executionStatus === 'retry' ? 'ðŸ”„' : 'âœ…';

  let message = `${emoji} *Workflow Execution ${status}*\n`;
  message += `*Workflow:* ${normalized.workflowNameSlack}\n`;
  message += `*Execution ID:* ${normalized.executionId}\n`;
  message += `*Mode:* ${normalized.executionMode}\n`;

  if (normalized.lastNodeExecuted) {
    message += `*Last Node:* ${normalized.lastNodeExecutedSlack}\n`;
  }

  if (normalized.retryOfExecutionId) {
    message += `*Retry of:* ${normalized.retryOfExecutionId}\n`;
  }

  if (normalized.hasError) {
    message += `*Error:* ${normalized.errorMessageSlack}\n`;
  }

  if (normalized.executionUrl) {
    message += `*Details:* ${normalized.executionUrl}`;
  }

  return message;
}

// Main Processing Loop
console.log(`Processing ${inputItems.length} execution data items`);

for (let itemIndex = 0; itemIndex < inputItems.length; itemIndex++) {
  const rawItem = inputItems[itemIndex];

  try {
    const item = rawItem.json;
    const processingTimestamp = new Date();

    // Extract execution data
    const execution = item.execution || {};
    const workflow = item.workflow || {};
    const error = execution.error || {};

    // Determine execution status
    const executionStatus = determineExecutionStatus(execution);
    const hasError = !!execution.error;
    const isRetry = !!execution.retryOf;

    // Build normalized object
    const normalized = {
      // Primary identifiers
      executionId: execution.id ? Number(execution.id) : null,
      workflowId: workflow.id ? String(workflow.id) : null,
      retryOfExecutionId: execution.retryOf ? Number(execution.retryOf) : null,

      // Workflow information
      workflowName: workflow.name || 'Unknown Workflow',
      workflowNameSanitized: sanitizeForSQL(workflow.name || 'Unknown Workflow'),
      workflowNameSlack: formatSlackText(workflow.name || 'Unknown Workflow', 100),

      // Execution details
      executionUrl: execution.url || null,
      executionUrlSanitized: sanitizeForSQL(execution.url || null),
      executionMode: execution.mode || 'unknown',
      executionStatus: executionStatus,

      // Node information
      lastNodeExecuted: execution.lastNodeExecuted || null,
      lastNodeExecutedSanitized: sanitizeForSQL(execution.lastNodeExecuted || null),
      lastNodeExecutedSlack: formatSlackText(execution.lastNodeExecuted || null, 50),

      // Error handling
      hasError: hasError,
      errorMessage: error.message || null,
      errorMessageSanitized: sanitizeForSQL(error.message || null),
      errorMessageSlack: formatSlackText(error.message || null, 200),

      errorStack: error.stack || null,
      errorStackSanitized: sanitizeForSQL(truncateText(error.stack, 2000)),
      errorStackTruncated: truncateText(error.stack, 500),

      // Status flags
      isRetry: isRetry,
      isError: hasError,
      isSuccess: executionStatus === 'success',
      isManualExecution: execution.mode === 'manual',
      isWebhookExecution: execution.mode === 'webhook',

      // Timestamps
      processedAt: processingTimestamp.toISOString(),
      processedAtUnix: Math.floor(processingTimestamp.getTime() / 1000),
      processedAtSQL: processingTimestamp.toISOString().slice(0, 19).replace('T', ' '),
      processedAtFriendly: processingTimestamp.toLocaleString('en-US', {
        year: 'numeric',
        month: 'short',
        day: 'numeric',
        hour: '2-digit',
        minute: '2-digit',
        second: '2-digit'
      }),

      // JSON fields for complex data storage
      executionDataJson: JSON.stringify({
        executionId: execution.id ? Number(execution.id) : null,
        workflowId: workflow.id,
        mode: execution.mode,
        retryOf: execution.retryOf ? Number(execution.retryOf) : null,
        url: execution.url
      }),

      errorDataJson: hasError ? JSON.stringify({
        message: error.message,
        stack: error.stack,
        lastNode: execution.lastNodeExecuted,
        timestamp: processingTimestamp.toISOString()
      }) : null,

      workflowMetadataJson: JSON.stringify({
        workflowId: workflow.id,
        workflowName: workflow.name,
        executionMode: execution.mode,
        processingTimestamp: processingTimestamp.toISOString()
      }),

      // Slack-ready message
      slackMessage: '',  // Will be populated below
      slackMessageShort: '',  // Will be populated below

      // Processing metadata
      processingMetadata: {
        normalizedAt: processingTimestamp.toISOString(),
        itemIndex: itemIndex,
        sourceNode: 'Execution Data Normalizer',
        version: '1.0',
        validationPassed: true,
        dataComplete: !!(execution.id && workflow.id && workflow.name)
      }
    };

    // Generate Slack messages
    normalized.slackMessage = generateSlackMessage(normalized);
    normalized.slackMessageShort = `${normalized.isError ? 'ðŸ”´' : 'âœ…'} ${normalized.workflowNameSlack} (${normalized.executionId}) - ${normalized.executionStatus.toUpperCase()}`;

    // Validation
    const missingFields = [];
    if (!normalized.executionId) missingFields.push('executionId');
    if (!normalized.workflowId) missingFields.push('workflowId');
    if (!normalized.workflowName || normalized.workflowName === 'Unknown Workflow') missingFields.push('workflowName');

    if (missingFields.length > 0) {
      normalized._error = {
        type: 'validation_error',
        message: `Missing critical fields: ${missingFields.join(', ')}`,
        missingFields: missingFields,
        originalData: {
          executionId: execution.id ? Number(execution.id) : null,
          workflowId: workflow.id,
          workflowName: workflow.name
        }
      };
      normalized.processingMetadata.validationPassed = false;
      normalized.processingMetadata.validationErrors = missingFields;
    }

    // Add to output with proper pairing
    outputItems.push({
      json: normalized,
      pairedItem: itemIndex
    });

  } catch (error) {
    console.error(`Error processing execution data item ${itemIndex}:`, error.message);

    // Create error item that maintains pairing
    const errorItem = {
      _error: {
        type: 'processing_error',
        message: error.message,
        stack: error.stack,
        itemIndex: itemIndex,
        timestamp: new Date().toISOString()
      },
      // Include minimal safe data for reference
      executionId: 'error',
      workflowId: 'error',
      workflowName: 'Processing Error',
      workflowNameSanitized: 'Processing Error',
      workflowNameSlack: 'Processing Error',
      executionStatus: 'processing_error',
      hasError: true,
      isError: true,
      errorMessage: 'Failed to normalize execution data',
      slackMessageShort: 'âŒ Processing Error - Could not normalize execution data',
      processingMetadata: {
        normalizedAt: new Date().toISOString(),
        itemIndex: itemIndex,
        validationPassed: false,
        error: true
      }
    };

    outputItems.push({
      json: errorItem,
      pairedItem: itemIndex
    });
  }
}

console.log(`Execution data normalization complete. Processed ${outputItems.length} items.`);

// Summary statistics
const successCount = outputItems.filter(item => !item.json._error).length;
const errorCount = outputItems.filter(item => item.json._error).length;
const executionErrors = outputItems.filter(item => item.json.isError && !item.json._error).length;
const retryExecutions = outputItems.filter(item => item.json.isRetry && !item.json._error).length;

console.log(`Processing Summary - Success: ${successCount}, Processing Errors: ${errorCount}`);
console.log(`Execution Summary - Failed Executions: ${executionErrors}, Retry Executions: ${retryExecutions}`);

return outputItems;

// n8n Code Node: Generate Slack Blocks for Execution Log
const inputItems = $input.all();
const outputItems = [];

// Helper function to get status emoji and color
function getStatusIndicators(executionStatus, hasError) {
  const indicators = {
    error: { emoji: "ðŸ”´", color: "#D93025", text: "FAILED" },
    retry: { emoji: "ðŸ”„", color: "#F9AB00", text: "RETRY" },
    success: { emoji: "âœ…", color: "#34A853", text: "SUCCESS" }
  };

  return indicators[executionStatus] || indicators.success;
}

// Helper function to truncate text safely for Slack
function truncateForSlack(text, maxLength = 150) {
  if (!text) return "N/A";
  if (text.length <= maxLength) return text;
  return text.substring(0, maxLength - 3) + "...";
}

// Process each execution item
for (let itemIndex = 0; itemIndex < inputItems.length; itemIndex++) {
  const item = inputItems[itemIndex].json;

  try {
    const status = getStatusIndicators(item.executionStatus, item.hasError);

    // Build the Slack blocks
    const blocks = [
      // Header with status emoji and workflow name
      {
        "type": "header",
        "text": {
          "type": "plain_text",
          "text": JSON.parse(JSON.stringify(`${status.emoji} ${item.workflowName || 'Unknown Workflow'}`)),
          "emoji": true
        }
      },

      // Main execution details in a single section
      {
        "type": "section",
        "text": {
          "type": "mrkdwn",
          "text": JSON.parse(JSON.stringify(`*Status:* ${status.text}\n*Execution ID:* ${item.executionId}\n*Mode:* ${item.executionMode?.toUpperCase() || 'UNKNOWN'}\n*Processed:* ${item.processedAtFriendly || 'Unknown'}`))
        }
      },

      // Conditional blocks based on execution status
      ...(item.lastNodeExecuted ? [{
        "type": "context",
        "elements": [
          {
            "type": "mrkdwn",
            "text": JSON.parse(JSON.stringify(`ðŸ“ Last executed node: *${item.lastNodeExecuted}*`))
          }
        ]
      }] : []),

      // Retry information if applicable
      ...(item.isRetry ? [{
        "type": "context",
        "elements": [
          {
            "type": "mrkdwn",
            "text": JSON.parse(JSON.stringify(`ðŸ”„ This is a retry of execution #${item.retryOfExecutionId}`))
          }
        ]
      }] : []),

      // Error details if applicable
      ...(item.hasError ? [
        {
          "type": "section",
          "text": {
            "type": "mrkdwn",
            "text": JSON.parse(JSON.stringify(`*Error Message:*\n\`\`\`${truncateForSlack(item.errorMessage, 300)}\`\`\``))
          }
        },
        // Error stack in a collapsible format
        ...(item.errorStack ? [{
          "type": "section",
          "text": {
            "type": "mrkdwn",
            "text": JSON.parse(JSON.stringify(`*Stack Trace:*\n\`\`\`${truncateForSlack(item.errorStackTruncated, 500)}\`\`\``))
          }
        }] : [])
      ] : []),

      // Success message for successful executions
      ...(!item.hasError ? [{
        "type": "section",
        "text": {
          "type": "mrkdwn",
          "text": JSON.parse(JSON.stringify(`âœ¨ Workflow completed successfully!`))
        }
      }] : []),

      // Divider before actions
      {
        "type": "divider"
      },

      // Action buttons
      {
        "type": "actions",
        "elements": [
          // View execution button (always present)
          ...(item.executionUrl ? [{
            "type": "button",
            "text": {
              "type": "plain_text",
              "text": "View Execution",
              "emoji": true
            },
            "style": "primary",
            "url": item.executionUrl
          }] : []),

          // Retry button for failed executions
          ...(item.hasError ? [{
            "type": "button",
            "text": {
              "type": "plain_text",
              "text": "Retry Execution",
              "emoji": true
            },
            "style": "danger",
            "action_id": `retry_execution_${item.executionId}`,
            "value": JSON.stringify({
              workflowId: item.workflowId,
              executionId: item.executionId
            })
          }] : []),

          // View workflow button
          {
            "type": "button",
            "text": {
              "type": "plain_text",
              "text": "View Workflow",
              "emoji": true
            },
            "action_id": `view_workflow_${item.workflowId}`,
            "value": item.workflowId
          }
        ]
      },

      // Footer context with metadata
      {
        "type": "context",
        "elements": [
          {
            "type": "mrkdwn",
            "text": JSON.parse(JSON.stringify(`ðŸ·ï¸ Workflow ID: ${item.workflowId} | ðŸ“Š Execution ID: ${item.executionId} | â° ${item.processedAtFriendly || 'Unknown'}`))
          }
        ]
      }
    ];

    // Create the complete Slack message payload
    const slackPayload = {
      // Top-level text for accessibility and notifications
      text: JSON.parse(JSON.stringify(`${status.emoji} Workflow "${item.workflowName || 'Unknown'}" ${status.text.toLowerCase()} - Execution #${item.executionId}`)),

      // Blocks for rich formatting
      blocks: blocks,

      // Message metadata
      metadata: {
        event_type: "n8n_execution_log",
        event_payload: {
          execution_id: item.executionId,
          workflow_id: item.workflowId,
          status: item.executionStatus,
          has_error: item.hasError,
          is_retry: item.isRetry
        }
      },

      // Unfurl settings
      unfurl_links: false,
      unfurl_media: false
    };

    // Output the normalized item with Slack payload
    outputItems.push({
      json: {
        ...item,

        // Add the Slack blocks and payload
        slackBlocks: blocks,
        slackPayload: slackPayload,
        slackText: slackPayload.text,

        // JSON string versions for direct API use
        slackBlocksJson: JSON.stringify(blocks),
        slackPayloadJson: JSON.stringify(slackPayload),

        // Processing metadata
        slackProcessingMetadata: {
          blocksGenerated: true,
          blockCount: blocks.length,
          hasActions: true,
          hasError: item.hasError,
          generatedAt: new Date().toISOString()
        }
      },
      pairedItem: itemIndex
    });

  } catch (error) {
    console.error(`Error generating Slack blocks for item ${itemIndex}:`, error.message);

    // Fallback simple message for errors
    const fallbackBlocks = [
      {
        "type": "section",
        "text": {
          "type": "mrkdwn",
          "text": JSON.parse(JSON.stringify(`âŒ *Error generating Slack message*\n\nExecution ID: ${item.executionId || 'Unknown'}\nWorkflow: ${item.workflowName || 'Unknown'}\nError: ${error.message}`))
        }
      }
    ];

    outputItems.push({
      json: {
        ...item,
        slackBlocks: fallbackBlocks,
        slackPayload: {
          text: JSON.parse(JSON.stringify(`Error processing execution log for ${item.workflowName || 'Unknown Workflow'}`)),
          blocks: fallbackBlocks
        },
        slackBlocksJson: JSON.stringify(fallbackBlocks),
        _error: {
          type: 'slack_blocks_generation_error',
          message: error.message
        }
      },
      pairedItem: itemIndex
    });
  }
}

console.log(`Generated Slack blocks for ${outputItems.length} execution logs`);

return outputItems;
