// n8n Code Node: Error Data Normalization
// Place this node immediately after the "When Executed by Another Workflow" trigger

const items = $input.all();
const outputItems = [];

// Helper functions for safe data extraction
function safeGet(obj, path, defaultValue = null) {
  return path.split('.').reduce((curr, prop) => 
    (curr && curr[prop] !== undefined) ? curr[prop] : defaultValue, obj);
}

function generateErrorLogId() {
  // Generate a proper UUID v4 for SQL Server uniqueidentifier
  return 'xxxxxxxx-xxxx-4xxx-yxxx-xxxxxxxxxxxx'.replace(/[xy]/g, function(c) {
    const r = Math.random() * 16 | 0;
    const v = c == 'x' ? r : (r & 0x3 | 0x8);
    return v.toString(16);
  });
}

function sanitizeForSQL(text) {
  if (!text) return null;
  return String(text)
    .replace(/'/g, "''")      // Escape single quotes
    .replace(/\\/g, "\\\\")   // Escape backslashes
    .replace(/\0/g, "")       // Remove null characters
    .trim();
}

function truncateText(text, maxLength) {
  if (!text) return null;
  const str = String(text);
  if (str.length <= maxLength) return str;
  return str.substring(0, maxLength - 3) + '...';
}

// Clean text function for display purposes (no SQL escaping)
function cleanText(text) {
  if (!text) return null;
  return String(text)
    .replace(/\0/g, "")       // Remove null characters
    .trim();
}

// Process each error item
for (let i = 0; i < items.length; i++) {
  const item = items[i];

  try {
    console.log(`Processing error item ${i + 1}/${items.length}`);

    // Get data from input parameters (preferred) or fallback to payload
    const triggerData = $('When Executed by Another Workflow').item.json;
    const errorData = safeGet(item, 'json.error') || safeGet(item, 'json');

    // Extract from input parameters with payload fallbacks
    const workflowName = triggerData.workflowName || 
                        safeGet(item, 'json.workflow.name') || 
                        'Unknown Workflow';

    const nodeName = triggerData.nodeName || 
                    safeGet(item, 'json.node.name') || 
                    null;

    const executionId = triggerData.executionId || 
                       safeGet(item, 'json.execution.id') || 
                       safeGet(item, 'json.executionId') || 
                       null;

    const workflowId = triggerData.workflowId || 
                      safeGet(item, 'json.workflow.id') || 
                      null;

    const mode = triggerData.mode || 
                safeGet(item, 'json.execution.mode') || 
                safeGet(item, 'json.mode') || 
                null;

    const relatedEntityId = triggerData.relatedEntityId || 
                           safeGet(item, 'json.relatedEntityId') || 
                           null;

    const relatedEntityType = triggerData.relatedEntityType || 
                             safeGet(item, 'json.relatedEntityType') || 
                             null;

    console.log('Raw error data type:', typeof errorData);
    console.log('Raw error data:', JSON.stringify(errorData, null, 2));

    // Normalize error data based on type
    let normalizedError = {
      message: null,
      name: null,
      stack: null,
      code: null,
      rawError: null
    };

    if (typeof errorData === 'string') {
      // Handle string errors
      normalizedError.message = errorData;
      normalizedError.name = 'StringError';
      normalizedError.rawError = errorData;

      // Try to extract more details from string if it contains structured info
      if (errorData.includes('Error:') && errorData.includes('node')) {
        const parts = errorData.split('Error:');
        if (parts.length > 1) {
          normalizedError.message = parts[1].trim();
        }
      }

    } else if (typeof errorData === 'object' && errorData !== null) {
      // Handle object errors
      normalizedError.message = errorData.message || 'Unknown error';
      normalizedError.name = errorData.name || 'ObjectError';
      normalizedError.stack = errorData.stack || null;
      normalizedError.code = errorData.code || null;
      normalizedError.rawError = JSON.stringify(errorData);

    } else {
      // Handle unexpected error formats
      normalizedError.message = 'Unknown error format';
      normalizedError.name = 'UnknownError';
      normalizedError.rawError = String(errorData);
    }

    // Build normalized output for SQL insertion
    const normalized = {
      // SQL Table Fields (with SQL sanitization)
      ErrorLogId: generateErrorLogId(),
      WorkflowName: truncateText(workflowName, 100),
      NodeName: truncateText(nodeName, 100),
      ErrorMessage: truncateText(normalizedError.message, 4000),
      ErrorDetails: truncateText(normalizedError.rawError, 4000),
      RelatedEntityId: relatedEntityId,
      RelatedEntityType: truncateText(relatedEntityType, 50),
      OccurredDate: new Date().toISOString(),
      IsResolved: false,
      WorkflowId: workflowId,
      StackTrace: truncateText(normalizedError.stack, 4000),
      Mode: truncateText(mode, 50),
      ExecutionId: truncateText(executionId, 150),

      // SQL-safe versions (only used for SQL insertion)
      WorkflowNameSanitized: sanitizeForSQL(workflowName),
      NodeNameSanitized: sanitizeForSQL(nodeName),
      ErrorMessageSanitized: sanitizeForSQL(normalizedError.message),
      ErrorDetailsSanitized: sanitizeForSQL(normalizedError.rawError),
      StackTraceSanitized: sanitizeForSQL(normalizedError.stack),

      // Slack context (clean but NOT SQL-escaped for proper JSON handling)
      slackContext: {
        errorType: normalizedError.name,
        errorCode: normalizedError.code,
        workflowName: cleanText(workflowName),
        nodeName: cleanText(nodeName) || 'Unknown Node',
        executionId: cleanText(executionId) || 'Unknown',
        timestamp: new Date().toISOString(),
        shortMessage: truncateText(cleanText(normalizedError.message), 200),
        hasStackTrace: !!normalizedError.stack
      },

      // Processing metadata
      processingMetadata: {
        normalizedAt: new Date().toISOString(),
        originalErrorType: typeof errorData,
        itemIndex: i,
        workflowNameSource: triggerData.workflowName ? 'input_parameter' : 'payload',
        nodeNameSource: triggerData.nodeName ? 'input_parameter' : 'payload',
        executionIdSource: triggerData.executionId ? 'input_parameter' : 'payload'
      }
    };

    // Validate required fields
    if (!workflowName || !normalized.ErrorMessage) {
      normalized._error = {
        type: 'validation_error',
        message: 'Missing required fields for error log',
        missingFields: {
          workflowName: !workflowName,
          errorMessage: !normalized.ErrorMessage
        }
      };
    }

    console.log(`Normalized error for ${workflowName}: ${normalized.ErrorMessage.substring(0, 100)}...`);

    outputItems.push({
      json: normalized,
      pairedItem: i
    });

  } catch (processingError) {
    console.error(`Failed to process error item ${i}:`, processingError.message);

    // Create minimal error item to maintain workflow flow
    outputItems.push({
      json: {
        ErrorLogId: generateErrorLogId(),
        WorkflowName: 'Error Processing Failed',
        NodeName: null,
        ErrorMessage: `Failed to process error data: ${processingError.message}`,
        ErrorDetails: JSON.stringify(item.json),
        RelatedEntityId: null,
        RelatedEntityType: null,
        OccurredDate: new Date().toISOString(),
        IsResolved: false,
        WorkflowId: null,
        StackTrace: processingError.stack,
        Mode: null,
        ExecutionId: null,

        _error: {
          type: 'processing_error',
          message: processingError.message,
          originalItem: item.json
        },

        slackContext: {
          errorType: 'ProcessingError',
          workflowName: 'Error Handler',
          nodeName: 'Error Normalization',
          executionId: 'Unknown',
          timestamp: new Date().toISOString(),
          shortMessage: `Processing failed: ${processingError.message}`,
          hasStackTrace: true
        }
      },
      pairedItem: i
    });
  }
}

console.log(`Processed ${outputItems.length} error items`);
return outputItems;

// n8n Code Node: Slack Error Notification Block Builder
// Place this after SQL insertion to send Slack notifications

const items = $('Error Normalization').all();

// Helper functions for Slack Block Kit
function safeSlackText(text, maxLength = 150) {
  if (!text) return "N/A";
  if (typeof text !== 'string') text = String(text);

  const truncated = text.length <= maxLength ? text : text.substring(0, maxLength - 3) + "...";

  return truncated
    .replace(/&/g, "&amp;")
    .replace(/</g, "&lt;")
    .replace(/>/g, "&gt;");
}

function createHeaderBlock(title) {
  return {
    "type": "header",
    "text": {
      "type": "plain_text",
      "text": safeSlackText(title, 150), // REMOVED JSON.stringify()
      "emoji": true
    }
  };
}

function createKeyValueSection(key, value) {
  return {
    "type": "rich_text",
    "elements": [
      {
        "type": "rich_text_section",
        "elements": [
          {
            "type": "text",
            "text": `${key}: `,
            "style": { "bold": true }
          },
          {
            "type": "text",
            "text": safeSlackText(value, 300) // REMOVED JSON.stringify()
          }
        ]
      }
    ]
  };
}

function createErrorSection(errorMessage, maxLength = 500) {
  return {
    "type": "section",
    "text": {
      "type": "mrkdwn",
      "text": `*Error Message:*\n\`\`\`${safeSlackText(errorMessage, maxLength)}\`\`\`` // REMOVED JSON.stringify()
    }
  };
}

function createContextBlock(items) {
  return {
    "type": "context",
    "elements": [
      {
        "type": "mrkdwn",
        "text": items.join(" | ") // REMOVED JSON.stringify()
      }
    ]
  };
}

// Validation function for Slack blocks
function validateSlackBlocks(blocks) {
  const issues = [];

  blocks.forEach((block, index) => {
    if (!block.type) {
      issues.push(`Block ${index}: Missing type field`);
    }

    if (block.type === 'header' && block.text?.text) {
      const textLength = block.text.text.length; // No need to JSON.parse since it's already a string
      if (textLength > 150) {
        issues.push(`Block ${index}: Header text too long (${textLength} chars)`);
      }
    }

    if (block.type === 'section' && !block.text?.text) {
      issues.push(`Block ${index}: Section block missing text`);
    }
  });

  return issues;
}

// Process each error item and create Slack payload
const slackPayloads = [];

items.forEach((item, index) => {
  try {
    const errorData = item.json;
    const context = errorData.slackContext || {};

    // Generate fallback text for accessibility
    const fallbackText = `üî¥ Workflow Error: ${context.workflowName || 'Unknown'} - ${context.shortMessage || 'Error occurred'} - Execution: ${context.executionId || 'Unknown'}`;

    // Determine error severity emoji
    const severityEmoji = errorData._error ? "‚ö†Ô∏è" : "üî¥";

    // Build blocks array
    const blocks = [
      // Header with workflow name and error indicator
      createHeaderBlock(`${severityEmoji} ${context.workflowName || 'Unknown Workflow'}`),

      // Primary error information
      createKeyValueSection("Status", "Failed"),
      createKeyValueSection("Node", context.nodeName || "Unknown Node"),
      createKeyValueSection("Error Type", context.errorType || "Unknown"),
    ];

    // Add error code if available
    if (context.errorCode) {
      blocks.push(createKeyValueSection("Error Code", context.errorCode));
    }

    // Add execution ID
    blocks.push(createKeyValueSection("Execution ID", context.executionId || "Unknown"));

    // Add error message section
    if (context.shortMessage) {
      blocks.push(createErrorSection(context.shortMessage, 500));
    }

    // Add stack trace info if available
    if (context.hasStackTrace) {
      blocks.push({
        "type": "section",
        "text": {
          "type": "mrkdwn",
          "text": "*Stack Trace:* Available in error logs" // REMOVED JSON.stringify()
        }
      });
    }

    // Visual separator
    blocks.push({ "type": "divider" });

    // Action buttons (if n8n web interface is accessible)
    const actionsBlock = {
      "type": "actions",
      "elements": []
    };

    // Add view logs button if we have execution ID
    if (context.executionId && context.executionId !== 'Unknown') {
      actionsBlock.elements.push({
        "type": "button",
        "text": {
          "type": "plain_text",
          "text": "View Execution"
        },
        "style": "primary",
        "url": `https://your-n8n-instance.com/workflow/${errorData.WorkflowId}/executions/${context.executionId}`
      });
    }

    // Add retry button (generic workflow restart)
    actionsBlock.elements.push({
      "type": "button",
      "text": {
        "type": "plain_text",
        "text": "View Workflow"
      },
      "style": "primary",
      "url": `https://your-n8n-instance.com/workflow/${errorData.WorkflowId}`
    });

    if (actionsBlock.elements.length > 0) {
      blocks.push(actionsBlock);
    }

    // Context footer with metadata
    const contextItems = [
      `üè∑Ô∏è ID: ${errorData.ErrorLogId.substring(0, 8)}`,
      `‚è∞ ${new Date(context.timestamp).toLocaleString()}`,
    ];

    if (errorData.Mode) {
      contextItems.push(`üìã Mode: ${errorData.Mode}`);
    }

    blocks.push(createContextBlock(contextItems));

    // Create final Slack payload
    const slackPayload = {
      text: fallbackText,
      blocks: blocks
    };

    // Validate blocks before adding to output
    const blockValidation = validateSlackBlocks(blocks);
    if (blockValidation.length > 0) {
      console.warn(`Block validation issues for item ${index}:`, blockValidation);

      // Create simple fallback if blocks are invalid
      slackPayload.blocks = [
        {
          "type": "section",
          "text": {
            "type": "mrkdwn",
            "text": `üî¥ *Workflow Error*\n\n*Workflow:* ${context.workflowName}\n*Error:* ${safeSlackText(context.shortMessage, 200)}\n*Execution:* ${context.executionId}` // REMOVED JSON.stringify()
          }
        }
      ];
    }

    slackPayloads.push({
      json: slackPayload,
      pairedItem: index
    });

  } catch (error) {
    console.error(`Failed to create Slack payload for item ${index}:`, error.message);

    // Create minimal fallback payload
    slackPayloads.push({
      json: {
        text: `üî¥ Error in workflow - Message generation failed`,
        blocks: [
          {
            "type": "section",
            "text": {
              "type": "mrkdwn",
              "text": `‚ùå *Message Generation Error*\n\nFailed to create notification for workflow error.\nPlease check the error logs for details.` // REMOVED JSON.stringify()
            }
          }
        ]
      },
      pairedItem: index
    });
  }
});

console.log(`Generated ${slackPayloads.length} Slack notifications`);
return slackPayloads;
